import React, { Fragment, useState } from 'react';

const Login = () => {
    const [formState, setFormState] = useState({ email: '', password: '' });

    // update state based on form input changes
    const handleChange = (event) => {
        const { name, value } = event.target;

        setFormState({
            ...formState,
            [name]: value,
        });
    };

    return (
        <Fragment>
            <div className="row">
                <div className="col s10 offset-s1 center-align">
                    <h5 id="title"><b>THE ASSESSMENT AND PROGRESS TRACKING APP</b></h5>
                </div>
            </div>
            <div className="row">
                <div className="col s6 offset-s1">
                    <h4><b>Sign In</b></h4>
                </div>
            </div>
            <div className="row">
                <form className="col s10 offset-s1">
                    <div className="input-field col s12">
                        <label for="email" className="active">Email</label>
                        <input
                            className="validate text-input"
                            placeholder="name@example.com"
                            name="email"
                            id="email"
                            type="email"
                            value={formState.email}
                            onChange={handleChange}
                        />
                    </div>
                    <div className="input-field col s12">
                        <label for="password" className="active">Password</label>
                        <input
                            className="validate"
                            placeholder="********"
                            name="password"
                            id="password"
                            type="password"
                            value={formState.password}
                            onChange={handleChange}
                        />
                    </div>
                    <button class="btn waves-effect waves-light col s12" type="submit" name="sign-in">Sign In
                    </button>
                </form>
            </div>
        </Fragment>
    );
};

export default Login
